import styled from "styled-components";

const StyledDiv = styled.div`
  display: flex;
  flex-wrap: wrap;
  justify-content: space-between;
`;

const Wrapper = styled.section`
  display: flex;
  justify-content: center;
  align-items: center;
  flex-direction: column;
  margin-top: 30px;
  width: 30%;
  height: 300px;
  font-size: 12pt;
  box-shadow: 1px 1px 3px 1px #c4c4c4;
`;

const Img = styled.img`
  border-radius: 50%;
  width: 200px;
  height: 200px;
  margin-bottom: 10px;
  box-shadow: 1px 5px 15px 0.5px #aaaaaad3;
  &:hover {
    cursor: pointer;
  }
`;

const TitleDiv = styled.div`
&:hover {
    cursor: pointer;
  }
`

const PostList = (props) => {
  const { post, onClickItem } = props;

  return (
    <StyledDiv>
      {post.map((item) => {
        return (
          <Wrapper key={'div'+item.id} >
            <Img
              key={'img'+item.id}
              src={
                item.img
                  ? item.img
                  : "https://upload.wikimedia.org/wikipedia/commons/thumb/a/ac/No_image_available.svg/1024px-No_image_available.svg.png"
              }
              onClick={() => onClickItem(item)}
            ></Img>
            <TitleDiv key={item.id} post={item} onClick={() => onClickItem(item)}>
              {item.title}
            </TitleDiv>
          </Wrapper>
        );
      })}
    </StyledDiv>
  );
};

export default PostList;
